<template>
  <div class="center">
    <section id="content">
      <h2 class="subheader">Mis Peliculas</h2>
      <p v-html="nameFull"></p>
      <p v-if="peliFavorita">Tu Peli es : {{peliFavorita.title}}</p>

      <!-- Estos si son pipes -->
      <h1>{{name | mayusculas | concatenarYear(' MI MSG PERSONAL') }}</h1>
      <!--Listado articulos-->
      <div id="articles">
        <!-- peliculasMayusculas es parecdio pipeado como angular -->
        <div v-for="(pelicula, index) in peliculasMayusculas" :key="index">
          <Pelicula :pelicula="pelicula"
          @favorita="recbireventodelhijo" />
        </div>
        <!-- Tambien se puede obtener el objeto como JAvascript -->
        <!-- {{peliculas[2].title}} -->
      </div>
    </section>
    <Sidebar />
  </div>
</template>

<script>
import Pelicula from "./Pelicula.vue";
import Sidebar from "./Sidebar.vue";
export default {
  name: "Peliculas",
 

  components: {
    Pelicula,
    Sidebar
  },
  filters:{
    mayusculas(value){
      return value.toUpperCase()
    },
    concatenarYear(value, msg){
      var date = new Date();

      return value + ' ' + date.getFullYear() + '' + msg;
    }
  },
  methods: {
    recbireventodelhijo(peli){
      // console.log("DESDE EL PADRE ", peli);
      this.peliFavorita = peli
    },
  
  },

// LOS COMPUTED SON COMO PIPES ANGULAR

  computed: {
    peliculasMayusculas(){
      var peliculasMod = this.peliculas;
      for (let i = 0; i < this.peliculas.length; i++) {
        peliculasMod[i].title = peliculasMod[i].title.toUpperCase(); 
      }
      return peliculasMod;
      //  var peliculasMod = this.peliculas.map(a=>a.title.toUpperCase())
      // return peliculasMod;
    },
    nameFull(){
      return `${this.name}</br> <strong>${this.apellido}</strong> `
    }
  },
  data() {

    return {
      name: 'Alex',
      apellido: 'Nova',
      peliFavorita: null,
      peliculas: [
        {
          title: "Batmas Vs Superman",
          year: 2017,
          image:
            "data:image/jpeg;base64,"
        },
        {
          title: "Red Social",
          year: 1850,
          image:
            "data:image/jpeg;base64,"
        },
        {
          title: "Ant Man",
          year: 2016,
          image:
            "data:image/jpeg;base64,"
        },
        {
          title: "OutBreak",
          year: 1995,
          image:
            "https://images-na.ssl-images-amazon.com/images/I/811RDdW8WhL._SL1500_.jpg"
        }
      ]
    };
  }
};
</script>

